mapper.insert:
INSERT INTO student_attendance( id, startdate, enddate, status,
student_id, create_by, create_date, update_by, update_date, del_flag
) VALUES ( #{id}, #{startdate}, #{enddate}, #{status},
#{studentId}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
UPDATE student_attendance SET startdate = #{startdate}, enddate = #{enddate}, status = #{status},
student_id = #{studentId}, update_by = #{updateBy.id}, update_date = #{updateDate}
WHERE id = #{id}

UPDATE student_attendance SET 
	del_flag = #{DEL_FLAG_DELETE}

WHERE id = #{id}
