mapper.select:
SELECT 

FROM student_attendance a

WHERE a.id = #{id}

SELECT 

FROM student_attendance a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM student_attendance a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE student_attendance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
