mapper.select:
SELECT

FROM student_lecture a

WHERE a.id = #{id}

SELECT

FROM student_lecture a



    a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${page.orderBy}



        ORDER BY a.update_date DESC

SELECT

FROM student_lecture a



    a.del_flag = #{DEL_FLAG_NORMAL}




        ORDER BY ${page.orderBy}



        ORDER BY a.update_date DESC

SELECT

FROM student_lecture a


    a.del_flag = #{DEL_FLAG_NORMAL}
    AND a.content LIKE ${a.content}%



        ORDER BY ${page.orderBy}



        ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO student_lecture( id, createrid, content, date, evaluation, score, advice, pm_id,
student_id, create_by, create_date, update_by, update_date, del_flag
) VALUES ( #{id}, #{createrid}, #{content}, #{date}, #{evaluation}, #{score}, #{advice},
#{pmId}, #{studentId}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
UPDATE student_lecture SET createrid = #{createrid}, content = #{content}, date = #{date}, evaluation = #{evaluation}, score = #{score}, advice = #{advice},
pm_id = #{pmId}, student_id = #{studentId}, update_by = #{updateBy.id}, update_date = #{updateDate}
WHERE id = #{id}

UPDATE student_lecture SET
del_flag = #{DEL_FLAG_DELETE}
        WHERE id = #{id}
