SELECT 

FROM student_praise a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class = #{classes}


		AND a.student = #{student}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM student_praise a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
