#{newclassUserid}, #{createBy.id}, #{createDate}, #{updateBy.id}, #{updateDate}, #{delFlag}
)

mapper.update:
UPDATE student_shiftclass SET shiftdate = #{shiftdate}, reason = #{reason}, classto_id = #{classtoId}, classfrom_id = #{classfromId}, oldclass_userid = #{oldclassUserid},
newclass_userid = #{newclassUserid}, update_by = #{updateBy.id}, update_date = #{updateDate}
